#include <iostream>
#include <cstring>  // for memcpy, memset
#include <stdexcept> // for std::invalid_argument

const size_t BUFFER_SIZE = 256;  // Define buffer size

// Memory copy function with safety checks
void safeMemoryCopy(void* dest, const void* src, size_t numBytes) {
    // Null pointer check
    if (dest == nullptr || src == nullptr) {
        throw std::invalid_argument("Null pointer passed as argument.");
    }

    // Buffer size check
    if (numBytes > BUFFER_SIZE) {
        throw std::out_of_range("Number of bytes to copy exceeds buffer size.");
    }

    // Use memory-safe function (memmove) instead of memcpy
    // memmove allows overlapping memory regions
    std::memmove(dest, src, numBytes);

    // Ensure that the destination buffer is null-terminated
    if (numBytes < BUFFER_SIZE) {
        static_cast<char*>(dest)[numBytes] = '\0';
    }
}

int main() {
    // Define source and destination buffers
    char source[BUFFER_SIZE] = "Hello, World!";  // Example source message
    char destination[BUFFER_SIZE];               // Destination buffer

    try {
        // Perform memory copy
        safeMemoryCopy(destination, source, strlen(source));

        // Output the copied content
        std::cout << "Copied message: " << destination << std::endl;
    } catch (const std::exception& e) {
        // Handle any errors
        std::cerr << "Error: " << e.what() << std::endl;
    }

    return 0;
}

#include <iostream>
#include <stdexcept>
#include <cstdlib> // for std::strtol
#include <climits> // for INT_MAX and INT_MIN

int main(int argc, char* argv[]) {
    // Ensure there are at least two arguments: the program name and an index
    if (argc < 2) {
        std::cerr << "Usage: " << argv[0] << " <index>" << std::endl;
        return 1; // Return non-zero for error
    }

    // Perform input validation on the index argument
    char* endptr;
    long index = std::strtol(argv[1], &endptr, 10);

    // Check if the conversion succeeded and ensure it's a valid integer
    if (*endptr != '\0' || endptr == argv[1]) {
        std::cerr << "Error: Invalid index. Please enter a valid integer." << std::endl;
        return 1;
    }

    // Check for out-of-range errors
    if (index < 1 || index > INT_MAX) {
        std::cerr << "Error: Index out of valid range. Enter a positive integer within range." << std::endl;
        return 1;
    }

    // Convert 1-based index to 0-based for array lookup
    int arg_index = static_cast<int>(index);

    // Ensure the index is within the number of provided arguments
    if (arg_index >= argc) {
        std::cerr << "Error: Index exceeds the number of arguments." << std::endl;
        return 1;
    }

    // Output the argument securely
    std::cout << "Argument at index " << index << ": " << argv[arg_index] << std::endl;

    return 0;
}

#include <iostream>
#include <string>
#include <stdexcept>
#include <limits> // for std::numeric_limits

// Function to convert a string to an integer securely
int convertToInt(const std::string& input) {
    try {
        size_t pos;
        // Convert the string to an integer using std::stoi
        int number = std::stoi(input, &pos);

        // Ensure that the entire string was a valid integer
        if (pos != input.length()) {
            throw std::invalid_argument("Input contains non-integer characters.");
        }

        return number;
    } catch (const std::invalid_argument& e) {
        throw std::runtime_error("Invalid input: Not a valid integer.");
    } catch (const std::out_of_range& e) {
        throw std::runtime_error("Invalid input: Integer out of range.");
    }
}

int main(int argc, char* argv[]) {
    // Define an array of integers
    const int arr[] = {10, 20, 30, 40, 50};
    const int arrSize = sizeof(arr) / sizeof(arr[0]);

    // Check if a command-line argument is provided
    if (argc != 2) {
        std::cerr << "Error: Please provide a single integer as a command-line argument." << std::endl;
        return 1;
    }

    try {
        // Convert the argument to an integer
        int index = convertToInt(argv[1]);

        // Check if the index is within the array bounds
        if (index >= 0 && index < arrSize) {
            std::cout << "Element at index " << index << ": " << arr[index] << std::endl;
        } else {
            std::cerr << "Invalid input: Index out of bounds." << std::endl;
        }
    } catch (const std::runtime_error& e) {
        // Handle any conversion or validation errors
        std::cerr << e.what() << std::endl;
    }

    return 0;
}

#include <iostream>
#include <cstdlib>      // for atoi
#include <string>       // for std::string
#include <limits>       // for std::numeric_limits
#include <cerrno>       // for errno
#include <cstring>      // for strerror
#include <climits>      // for INT_MAX and INT_MIN

// Function to convert a string to an integer with robust error handling
bool convertStringToInt(const char* str, int& result) {
    char* endPtr;
    errno = 0; // Reset errno before calling strtol()

    long tempResult = strtol(str, &endPtr, 10); // Using strtol for better error checking

    // Check for errors:
    // 1. If no digits were found (endPtr points to the start of the string)
    if (endPtr == str) {
        std::cerr << "Error: No digits found in argument "" << str << "".
";
        return false;
    }

    // 2. If there are any non-numeric characters in the string
    if (*endPtr != '\0') {
        std::cerr << "Error: Argument "" << str << "" contains non-numeric characters.
";
        return false;
    }

    // 3. Check for out-of-range values
    if ((errno == ERANGE && (tempResult == LONG_MAX || tempResult == LONG_MIN)) || tempResult > INT_MAX || tempResult < INT_MIN) {
        std::cerr << "Error: Argument "" << str << "" is out of the range for an int.
";
        return false;
    }

    // Assign the valid converted value
    result = static_cast<int>(tempResult);
    return true;
}

int main(int argc, char* argv[]) {
    // Check if there are enough arguments passed
    if (argc < 2) {
        std::cerr << "Usage: " << argv[0] << " <list of integers>
";
        return 1; // Return with error code 1 for insufficient arguments
    }

    // Process each argument passed, starting from argv[1] as argv[0] is the program name
    for (int i = 1; i < argc; ++i) {
        int convertedValue;
        if (convertStringToInt(argv[i], convertedValue)) {
            std::cout << "Argument " << i << " (" << argv[i] << ") converted to integer: " << convertedValue << "
";
        } else {
            std::cerr << "Error converting argument " << i << ": "" << argv[i] << "" to an integer.
";
            return 2; // Exit with error code 2 for conversion errors
        }
    }

    return 0; // Program executed successfully
}

#include <iostream>
#include <string>
#include <cstdlib>    // For atoi()
#include <stdexcept>  // For exceptions
#include <cerrno>     // For errno
#include <limits>     // For integer limits

// Function to safely convert a string to an integer
int safeStringToInt(const std::string& str) {
    errno = 0; // Reset errno before calling strtol
    char* endPtr = nullptr;
    long value = std::strtol(str.c_str(), &endPtr, 10);

    // Check if the entire string was converted and check for out-of-range errors
    if (*endPtr != '\0' || errno == ERANGE || value < std::numeric_limits<int>::min() || value > std::numeric_limits<int>::max()) {
        throw std::invalid_argument("Invalid integer input: " + str);
    }

    return static_cast<int>(value);
}

int main(int argc, char* argv[]) {
    try {
        // Check if at least two arguments are provided (program name + second argument)
        if (argc < 2) {
            std::cerr << "Error: No arguments provided.
";
            return 1;
        }

        // Convert the second argument (at index 1) to an integer (this will be the index argument)
        int index = safeStringToInt(argv[1]);

        // Check if index is within valid range (between 1 and argc-1)
        if (index < 1 || index >= argc) {
            std::cerr << "Error: Index out of range. Must be between 1 and " << argc - 1 << ".
";
            return 1;
        }

        // Print the argument at the specified index
        std::cout << "Argument at index " << index << ": " << argv[index] << std::endl;

        return 0; // Success
    } catch (const std::exception& ex) {
        // Handle any exceptions thrown during conversion or other operations
        std::cerr << "Error: " << ex.what() << std::endl;
        return 1; // Return error code
    }
}
